SELECT RPAD('010-2036-', 13, '*') AS PHONE FROM DUAL;
SELECT RPAD(SUBSTR('010-2036-8094', 1, INSTR('010-2036-8094', '-', 1, 2)), 13, '*') AS PHONE FROM DUAL;
SELECT RPAD('920213-', 13, '*') AS SSN FROM DUAL;
SELECT RPAD(SUBSTR('920213-1548013', 1, INSTR('90213-1548013', '-')), 14, '*') AS SSN FROM DUAL;
SELECT CONCAT(CONCAT('사원번호:', EMPNO), CONCAT(' 이름:', ENAME)) AS EMP_INFO FROM EMP;
SELECT CONCAT(CONCAT(CONCAT(CONCAT(CONCAT(CONCAT(CONCAT('사원번호: ' || EMPNO, ', 이름: ' || ENAME),', 직업: ' || JOB), ', 직속상관: ' || NVL(TO_CHAR(MGR), '없음')), ', 회사입사일: ' || HIREDATE), ', 급여: ' || SAL), ', 상여금: ' || NVL(TO_CHAR(COMM), 0)), ', 부서번호: ' || DEPTNO) AS "사원 정보" FROM EMP;
SELECT CONCAT(CONCAT(CONCAT(CONCAT(CONCAT(CONCAT('사원번호:', EMPNO), CONCAT(' 이름:', ENAME)), CONCAT(' 직업:', JOB)), CONCAT(' 직속상관:', NVL(TO_CHAR(MGR), '없음'))), CONCAT(' 입사일:', HIREDATE)), CONCAT(CONCAT(' 연봉:', SAL * 12), CONCAT(' 부서번호:', DEPTNO))) AS "사원 연봉 정보" FROM EMP;
SELECT DEPTNO FROM EMP WHERE MOD(DEPTNO, 2) = 0;
SELECT TRUNC(SAL/7,5) FROM EMP;
SELECT ROUND(AVG(SAL * 12), 5) AS 연봉 FROM EMP;